import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
  KeyboardAvoidingView,
  Platform,
} from "react";
import { LoginStyles } from "./LoginStyles";

const LoginScreen = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = () => {
    if (!username || !password) {
      setError("Please fill in all fields");
      return;
    }
    // Handle login logic here
    console.log("Username:", username);
    console.log("Password:", password);
  };

  return (
    <KeyboardAvoidingView
      behavior={Platform.OS === "ios" ? "padding" : "height"}
      style={LoginStyles.container}
    >
      {/* <View style={LoginStyles.logoContainer}>
        <Image
          source={require('../../assets/logo.png')} // Add your logo image
          style={LoginStyles.logo}
          resizeMode="contain"
        />
        <Text style={LoginStyles.title}>Welcome Back</Text>
      </View> */}

      <View style={LoginStyles.formContainer}>
        <View style={LoginStyles.inputGroup}>
          <Text style={LoginStyles.label}>Username</Text>
          <TextInput
            style={LoginStyles.input}
            value={username}
            onChangeText={(text) => {
              setUsername(text);
              setError("");
            }}
            placeholder="Enter your username"
            autoCapitalize="none"
          />
        </View>

        <View style={LoginStyles.inputGroup}>
          <Text style={LoginStyles.label}>Password</Text>
          <TextInput
            style={LoginStyles.input}
            value={password}
            onChangeText={(text) => {
              setPassword(text);
              setError("");
            }}
            placeholder="Enter your password"
            secureTextEntry
          />
        </View>

        {error ? <Text style={LoginStyles.errorText}>{error}</Text> : null}

        <TouchableOpacity style={LoginStyles.button} onPress={handleSubmit}>
          <Text style={LoginStyles.buttonText}>Sign In</Text>
        </TouchableOpacity>

        <TouchableOpacity style={LoginStyles.forgotPassword}>
          <Text style={LoginStyles.forgotPasswordText}>
            Forgot Password?
          </Text>
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>
  );
};

export default LoginScreen;
