export const generateWeeks = (year) => {
  const weeks = [];
  const startDate = new Date(year, 0, 1);
  const endDate = new Date(year, 11, 31);

  // Adjust start date to first Monday if necessary
  while (startDate.getDay() !== 1) {
    startDate.setDate(startDate.getDate() + 1);
  }

  let currentDate = new Date(startDate);
  let weekNumber = 1;

  while (currentDate <= endDate) {
    const weekStart = new Date(currentDate);
    const weekEnd = new Date(currentDate);
    weekEnd.setDate(weekEnd.getDate() + 4); // Add 4 days to get to Friday

    const weekString = `Week ${String(weekNumber).padStart(2, '0')} (${weekStart.toLocaleDateString('en-GB', { 
      day: '2-digit',
      month: 'short'
    })} Mon - ${weekEnd.toLocaleDateString('en-GB', {
      day: '2-digit',
      month: 'short'
    })} Fri)`;

    weeks.push({
      weekNumber,
      weekString,
      startDate: weekStart,
      endDate: weekEnd,
      workDays: [
        { day: 'Monday', date: new Date(weekStart) },
        { day: 'Tuesday', date: new Date(weekStart.setDate(weekStart.getDate() + 1)) },
        { day: 'Wednesday', date: new Date(weekStart.setDate(weekStart.getDate() + 1)) },
        { day: 'Thursday', date: new Date(weekStart.setDate(weekStart.getDate() + 1)) },
        { day: 'Friday', date: new Date(weekStart.setDate(weekStart.getDate() + 1)) }
      ]
    });

    // Move to next Monday
    currentDate.setDate(currentDate.getDate() + 7);
    weekNumber++;
  }

  return weeks;
};
